// src/Auth.js
import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from './AuthContext';

const Auth = () => {
  const [isLogin, setIsLogin] = useState(true); // Toggle between login and signup
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const { setUser } = useContext(AuthContext);

  const handleAuth = (e) => {
    e.preventDefault();
    // In a real app, you would call your backend API here.
    // For now, we simply set the user and navigate to home.
    setUser({ name: email.split('@')[0] }); // example: using part of the email as user name
    navigate('/home');
  };

  const handleSkip = () => {
    // Allow users to proceed as guests.
    setUser({ name: 'Guest' });
    navigate('/home');
  };

  return (
    <div className="flex flex-col justify-center items-center min-h-screen bg-gradient-to-r from-orange-100 to-red-100">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h2 className="text-3xl font-bold mb-6 text-center">
          {isLogin ? 'Login' : 'Sign Up'}
        </h2>
        <form onSubmit={handleAuth}>
          <div className="mb-4">
            <label className="block text-gray-700">Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded mt-1"
              required
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700">Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded mt-1"
              required
            />
          </div>
          <button
            type="submit"
            className="w-full bg-orange-500 text-white p-2 rounded hover:bg-orange-600 mb-4"
          >
            {isLogin ? 'Login' : 'Sign Up'}
          </button>
        </form>
        <div className="flex justify-between">
          <button
            onClick={() => setIsLogin(!isLogin)}
            className="text-blue-500 hover:underline focus:outline-none"
          >
            {isLogin ? 'Switch to Sign Up' : 'Switch to Login'}
          </button>
          <button
            onClick={handleSkip}
            className="text-green-500 hover:underline focus:outline-none"
          >
            Skip
          </button>
        </div>
      </div>
    </div>
  );
};

export default Auth;
